mapper.update:
UPDATE atmosphere SET open = #{openValue}
and id in
#{item}
AND observation_time LIKE '%' || #{dateSelected} || '%'
AND filename LIKE '%' || #{filepath} || '%'
and status=0

mapper.select:
SELECT * FROM atmosphere


        and observation_time LIKE '%' ||#{time} || '%'


        AND device_id = #{device}

    AND status=0

order by observation_time;

SELECT *  FROM atmosphere


                  and observation_time LIKE '%' ||#{time} || '%'


                  AND device_id = #{device}

          and status=0

        ORDER BY observation_time DESC LIMIT 1;
