mapper.update:
UPDATE model_status
SET usage_status = #{modelStatusClass.usageStatus}
WHERE id = (
SELECT id FROM (
SELECT id FROM model_status


        model_name = #{modelStatusClass.modelName}


        AND user_name = #{modelStatusClass.userName}


        AND create_userid = #{modelStatusClass.createUserid}


        and observation_time=#{modelStatusClass.observationTime}


        and start_time=#{modelStatusClass.startTime}


        and end_time=#{modelStatusClass.endTime}

    AND status = 0

ORDER BY id DESC
LIMIT 1
) AS temp
)

update model_status set status=1


        and id in

            #{item}



        model_name = #{modelStatusClass.modelName}


        and user_name = #{modelStatusClass.userName}


        and create_userid=#{modelStatusClass.createUserid}


        and observation_time=#{modelStatusClass.observationTime}


        and start_time=#{modelStatusClass.startTime}


        and end_time=#{modelStatusClass.endTime}

    and status =0

mapper.select:
select "user_name" as "userName","create_userid" as "createUserid",
       "usage_status" as "usageStatus","create_time" as "createTime",
       "function_selected" as "functionSelected","class_name" as "className",
       "observation_time" as "observationTime","model_name" as "modelName"
  from model_status


        model_name = #{modelStatusClass.modelName}


        and user_name = #{modelStatusClass.userName}


        and usage_status = #{modelStatusClass.usageStatus}


        and create_userid=#{modelStatusClass.createUserid}


        and observation_time=#{modelStatusClass.observationTime}


        and start_time=#{modelStatusClass.startTime}


        and end_time=#{modelStatusClass.endTime}

    and status =0

select "user_name" as "userName","create_userid" as "createUserid",
"usage_status" as "usageStatus","create_time" as "createTime",
"function_selected" as "functionSelected","class_name" as "className",
"observation_time" as "observationTime","model_name" as "modelName"
from model_status


        model_name = #{modelStatus.modelName}


